import Axios from "axios";
import React, { useEffect, useState } from "react";
import './popup.css'
import {Progress} from "reactstrap";
import Loader from "./loader";
import Waveloader from "./waveloader";

const Sectionimage = props => {
    const [data, setdata] = useState([]);
    const [file, setFile] = useState(null);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [chnamadata, setChnamadata] = useState("");
    const [chgambardata, setChgambardata] = useState("");
    const [chiddata, setChiddata] = useState("");
    const [nfound, setNfound] = useState(false);
    const [search, setSearch] = useState("");
    const [load, setLoad] = useState(true);
    const [isdelete, setIsdelete] = useState(false);
    const url = "http://192.168.6.235:3001/";

    const getdata = async (id) => {
        let tek2 = "%"+id+"%";
        console.log(tek2);
        const get = await Axios.put(url+props.getdb, {nama: tek2})
        .then((response) => {
            if(response.data.length == 0) setNfound(true);
            else setNfound(false);
            setdata(response.data);
        })
        setLoad(false);
    };

    function handleChangeSearchK (event) {
        setLoad(true)
        event.preventDefault();
        console.log(search);
        getdata(search);
    }

    useEffect(() => {
        getdata([]);
    }, [])

    const toggleEditKPopup = () => {
        setIsEditOpen(!isEditOpen);
        if(isEditOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
            setIschange(false);
            setFile(null);
        }
    }

    const toggleAddKPopup = () => {
        setIsAddOpen(!isAddOpen);
        if(isAddOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
            setFile(null);
        }
    }

    const toggleDelete = () => {
        setIsdelete(!isdelete);
        if(isdelete === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
            setFile(null);
        }
    }

    const getdataId = async(id) => {
        Axios.put(url+ props.getdbId, {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChiddata(val[props.id]);
                    console.log(chiddata);
                    setChnamadata(val[props.nama]);
                    console.log(chnamadata);
                    setChgambardata(val[props.gambar]);
                    console.log("jalan")
                })}
            })
       
        toggleEditKPopup();
    }

    const getdataDelete = async (id) => {
        Axios.put(url+ props.getdbId, {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChiddata(val[props.id]);
                    console.log(chiddata);
                    setChnamadata(val[props.nama]);
                    console.log(chnamadata);
                    setChgambardata(val[props.gambar]);
                })}
            })
        toggleDelete();
    }

    const handleNamadataChange = (event) => {
        setChnamadata(event.target.value);
        console.log(chnamadata);
    }

    const [ischange, setIschange] = useState(false);

    const handleGambardataChange = async (e) => {
        try{
            setIschange(true);
            setFile(e.target.files[0]);
            const base64K = await convertToBase64(e.target.files[0]);
            setChgambardata(base64K);
            console.log(base64K);
        } catch{
            setChgambardata(null)
        }
        
        // const imageu = document.getElementById("imageupdate");
        // const formData = new FormData()
        // formData.append("name", chnamadata);
        // formData.append("image", file);
