import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import './edit.css'
import './editProfil.css'
import './editKategori.css'
import './editOffice.css'
// import axios, { Axios } from "axios";
import Axios from "axios";
import Sectionimage from "./sectionimage";
import {useSelector, useDispatch} from "react-redux";
import { useNavigate } from "react-router-dom";
import { getMe, LogOut, reset } from "./auth";
import Navbar from "./navbar";
import Crop from "./imageresize";



export default function Edit () {

    const {isError, isSuccess} = useSelector(
        (state) => state.auth
    );
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [profil, setProfil] = useState("");
    const [visi, setVisi] = useState("");
    const [misi, setMisi] = useState("");
    const [office, setOffice] = useState([]);
    const [alamatfooter, setAlamatfooter] = useState("");
    const [emailfooter, setEmailfooter] = useState("");
    const [telpfooter, setTelpfooter] = useState("");
    const [whatsappfooter, setWhatsappfooter] = useState("");

    const getProfil = async () => {
            Axios.put("http://192.168.6.235:3001/getprofil", {id: 1})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setProfil(val.profildesc);
                })}
            })
    }
    const getVisi = async () => {
        Axios.put("http://192.168.6.235:3001/getprofil", {id: 2})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setVisi(val.profildesc);
                })}
            })
    }
    const getMisi = async () => {
        Axios.put("http://192.168.6.235:3001/getprofil", {id: 3})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setMisi(val.profildesc);
                })}
            })
    }

    const getOffice = async () => {
        Axios.get("http://192.168.6.235:3001/getoffice").then((response) => {
            setOffice(response.data);
        })
    }

    const getFooter = async () => {
        Axios.get("http://192.168.6.235:3001/getfooter")
        .then((response) => {
            const dataf = response.data;
            {dataf.map((val) => {
                setAlamatfooter(val.jalan_kantor);
                setEmailfooter(val.email);
                setTelpfooter(val.no_telp);
                setWhatsappfooter(val.whatsapp);
            })}
        })
    }

    
    
    useEffect(() => {
        console.log(isSuccess);
        dispatch(getMe());
    }, [dispatch]);
    

    useEffect(()=> {
        console.log(isError);
        if(isError || isSuccess === false){
            navigate("/")
        }
        // if(width < 930) setNavbar(false);
        getProfil("");
        console.log(profil);
        getVisi("");
        console.log(visi);
        getMisi("");
        console.log(misi);
        getOffice([]);
        console.log(office);
        getFooter([]);
    }, [isError]  ,[], [])



    const [isAddOOpen, setIsAddOOpen] = useState(false);
    const [isEditOOpen, setIsEditOOpen] = useState(false);
    const [chidoffice, setChidoffice] = useState("");
    const [chkotaoffice, setChkotaoffice] = useState("");
    const [chjalanoffice, setChjalanoffice] = useState("");
    const [chlatoffice, setChlatoffice] = useState("");
    const [chlongoffice, setChlongoffice] = useState("");


    const toggleAddOPopup = () => {
        setIsAddOOpen(!isAddOOpen);
        if(isAddOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const toggleEditOPopup = () => {
        setIsEditOOpen(!isEditOOpen);
        if(isEditOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const getOfficeId = (id) => {
        Axios.put("http://192.168.6.235:3001/getofficebyid", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChidoffice(val.ID_OFFICE);
                    setChkotaoffice(val.kota);
                    console.log(chkotaoffice);
                    setChjalanoffice(val.jalan);
                    console.log(chjalanoffice);
                    setChlatoffice(val.latitude);
                    setChlongoffice(val.longitude);
                    console.log("jalan")
                })}
            })
        
            toggleEditOPopup();
    }


    const handleProfilChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setProfil(event.target.value);
        const btn = document.getElementById('profilbtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const handleVisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setVisi(event.target.value);
        const btn = document.getElementById('visibtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const handleMisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setMisi(event.target.value);
        const btn = document.getElementById('misibtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const updateProfil = () => {
        Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: profil, id: 1})
        setProfil(profil);
        alert("Data berhasil diupdate");
        const btn = document.getElementById('profilbtn');
        btn.style="display: none"
    };

    const updateVisi = (id) => {
        Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: visi, id: 2})
        setVisi(visi);
        alert("Data berhasil diupdate")
        const btn = document.getElementById('visibtn');
        btn.style="display: none"
    };

    const updateMisi = (id) => {
        Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: misi, id: 3})
        setMisi(misi)
        alert("Data berhasil diupdate")
        const btn = document.getElementById('misibtn');
        btn.style="display: none"
    };

    const updateOffice = (id, e) => {
        e.preventDefault();
        console.log("hai")
        Axios.put("http://192.168.6.235:3001/updateoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice ,id : id})
        // getOffice();
        window.location.reload();
        toggleEditOPopup();
};

const deleteOffice = (id) => {
    console.log("hai");
    Axios.put("http://192.168.6.235:3001/deleteoffice", {id: id});
    window.location.reload();
}

const addOffice = (e) => {
    e.preventDefault();
    console.log("hai");
    Axios.post("http://192.168.6.235:3001/addoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice})
        // getKategori();
        // window.location.reload();
}

const updateFooter = (e) => {
    e.preventDefault();
    console.log("hai")
    Axios.put("http://192.168.6.235:3001/updatefooter", {alamat: alamatfooter, email: emailfooter, telp: telpfooter, wa: whatsappfooter ,id : 1})
};




return(
        <div className="editor">
            <Navbar/>

            <div className="edit-container" id="editor">
                    <div className="profil-container" id="profil">
                        <div className="row">
                            <h1 className="Profil">Profil</h1>
                            <h1 className="button" onClick={() => updateProfil()}  id='profilbtn'>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} value={profil}  onChange={handleProfilChange}></textarea>
                        </form>
                    </div>

                    <div className="profil-container" id="visi">
                        <div className="row">
                            <h1 className="Profil">Visi</h1>
                            <h1 className="button" onClick={() => updateVisi()} id='visibtn'>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} value={visi}  onChange={handleVisiChange}></textarea>
                        </form>
                    </div>

                    <div className="profil-container" id="misi">
                        <div className="row">
                            <h1 className="Profil">Misi</h1>
                            <h1 className="button" onClick={() => updateMisi()} id='misibtn'>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} value={misi}  onChange={handleMisiChange}></textarea>
                        </form>
                    </div>

                    <Sectionimage 
                        getdb = "getkategoribynama"
                        getdbId = "getkategoribyid"
                        update = "updatekategori"
                        add = "addkategori"
                        delete = "deletekategori"
                        title = "Kategori"
                        nama = "nama_kategori"
                        gambar = "gambar_kategori"
                        id = "id_kategori"
                    />
                

                    <div className="visimisi-container" id="office">
                        <div className="row">
                            <h1 className="profiil">Office</h1>
                            <h1 className="button add" onClick={() => toggleAddOPopup()}>tambah</h1>
                        </div>
                        {isAddOOpen && <>
                            
                            <div className="popup-box">
                                                        <div className="box">
                                                            <span className="close-icon" onClick={toggleAddOPopup}>x</span>
                                                            <div className="edit-office-container">

                                                            <form onSubmit={(event) =>  addOffice(event)}>
                                                                <label>Kota</label>
                                                                <input required type='text' placeholder="Kota" value={chkotaoffice} onChange={(event) => {setChkotaoffice(event.target.value)}}></input>
                                                                <label>Alamat</label>
                                                                <input required type='text' placeholder="Jalan" value={chjalanoffice} onChange={(event) => {setChjalanoffice(event.target.value)}}></input>
                                                                <div className="row">
                                                                    <div className="col">
                                                                        <label>Latitude</label>
                                                                        <input required type='text' placeholder="Latitude" value={chlatoffice} onChange={(event) => {setChlatoffice(event.target.value)}}></input>
                                                                    </div>
                                                                    <div className="col">
                                                                        <label>Longitude</label>
                                                                        <input required type='text' placeholder="Longitude" value={chlongoffice} onChange={(event) => {setChlongoffice(event.target.value)}}></input>
                                                                    </div>
                                                                </div>
                                                                <input type='submit' value='save'></input>
                                                            </form>

                                                        </div>
                                                    </div>
                                                    
                                                    </div>
                        </>}
                        <div className="office-editor-container" >
                            {office.map((val) => {
                                return(
                                    <div className="edit-office-container">
                                        <form>
                                            <label>Kota</label>
                                            <input readOnly type='text' value={val.kota}></input>
                                            <label>Alamat</label>
                                            <input readOnly type='text' value={val.jalan}></input>
                                            <div className="row">
                                                <div className="col">
                                                    <label>Latitude</label>
                                                    <input readOnly type='text' value={val.latitude}></input>
                                                </div>
                                                <div className="col">
                                                    <label>Longitude</label>
                                                    <input readOnly type='text' value={val.longitude}></input>
                                                </div>
                                            </div>
                                            <div className="row-button">
                                                <h1 className="button edit" onClick={() => getOfficeId(val.ID_OFFICE)}>edit</h1>
                                                <h1 className="button delete" onClick={() => deleteOffice(val.ID_OFFICE)}>delete</h1>
                                            </div>
                                            
                                        </form>
                                        {isEditOOpen && <>
                                                <div className="popup-box">
                                                    <div className="box">
                                                        <span className="close-icon" onClick={toggleEditOPopup}>x</span>
                                                        <form onSubmit={(event) =>  updateOffice(chidoffice, event)}>
                                                            <label>Kota</label>
                                                            <input required type='text' placeholder="Kota" value={chkotaoffice} onChange={(event) => {setChkotaoffice(event.target.value)}}></input>
                                                            <label>Alamat</label>
                                                            <input required type='text' placeholder="Jalan" value={chjalanoffice} onChange={(event) => {setChjalanoffice(event.target.value)}}></input>
                                                            <div className="row">
                                                                <div className="col">
                                                                    <label>Latitude</label>
                                                                    <input required type='text' placeholder="Latitude" value={chlatoffice} onChange={(event) => {setChlatoffice(event.target.value)}}></input>
                                                                </div>
                                                                <div className="col">
                                                                    <label>Longitude</label>
                                                                    <input required type='text' placeholder="Longitude" value={chlongoffice} onChange={(event) => {setChlongoffice(event.target.value)}}></input>
                                                                </div>
                                                            </div>
                                                            <input type='submit' value='save'></input>
                                                        </form>

                                                    </div>
                                                </div>

                                            </>}
                                    </div>
                                )
                            })}
                            
                        </div>
                    </div>

                    <Sectionimage 
                        getdb = "getmitrabynama"
                        getdbId = "getmitrabyid"
                        update = "updatemitra"
                        add = "addmitra"
                        delete = "deletemitra"
                        title = "Mitra"
                        nama = "nama_mitra"
                        gambar = "gambar_mitra"
                        id = "ID_MITRA"
                    />

                    <Sectionimage 
                        getdb = "getpartnerbynama"
                        getdbId = "getpartnerbyid"
                        update = "updatepartner"
                        add = "addpartner"
                        delete = "deletepartner"
                        title = "Partner"
                        nama = "nama_partner"
                        gambar = "gambar_partner"
                        id = "ID_PARTNER"
                    />


                    <div className="visimisi-container" id="kontak">
                        <h1 className="profil">Kontak</h1>
                        <div className="office-editor-container footer">
                        <div className="edit-office-container">
                                    <form onSubmit={(event) => updateFooter(event)}>
                                            <label>Alamat Kantor Pusat</label>
                                            <input type='text' value={alamatfooter} onChange={(event) => setAlamatfooter(event.target.value)}></input>
                                            <label>Email</label>
                                            <input type='text' value={emailfooter} onChange={(event) => setEmailfooter(event.target.value)}></input>
                                            <div className="row">
                                                <div className="col">
                                                    <label>No. Telepon</label>
                                                    <input type='text' value={telpfooter} onChange={(event) => setTelpfooter(event.target.value)}></input>
                                                </div>
                                                <div className="col">
                                                    <label>Whatsapp</label>
                                                    <input type='text' value={whatsappfooter} onChange={(event) => setWhatsappfooter(event.target.value)}></input>
                                                </div>
                                            </div>
                                            <input type='submit' value='save changes' className='footer-btn'></input>
                                            
                                        </form>
                                        
                        </div>
                            
                        </div>
                    </div>

                {/* <div className="profil-container">
                    <h1 className="Profil">Profil</h1>
                    <form>
                        <textarea rows={10} id='profil' style={{height: `auto`}} value={formProfil.profildesc} onChange={handleProfil}></textarea>
                    </form>
                </div>

                <div className="visimisi-container">
                    <h1 className="Profil">Visi</h1>
                    <form>
                        <textarea rows={8} onChange={handlePr
                            ofil} id='visi' value={formProfil.visidesc}></textarea>
                    </form>
                </div>

                <div className="visimisi-container">
                    <h1 className="Profil">Misi</h1>
                    <form>
                        <textarea rows={8} onChange={handleProfil} id='misi' value={formProfil.misidesc}></textarea>
                    </form>
                </div> */}

                


            </div>


        </div>
    )
}
