import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import './edit.css'
import './editProfil.css'
import './editKategori.css'
import './editOffice.css'
// import axios, { Axios } from "axios";
import Axios from "axios";
import Sectionimage from "./sectionimage";
import {useSelector, useDispatch} from "react-redux";
import { useNavigate } from "react-router-dom";
import { getMe, LogOut, reset } from "./auth";
import Navbar from "./navbar";
import Crop from "./imageresize";
import Loader from "./loader";
import SectionTable from "./sectiontable";
import {Editor} from '@tinymce/tinymce-react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';



export default function Edit () {

    const {isError, isSuccess} = useSelector(
        (state) => state.auth
    );
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [profil, setProfil] = useState("");
    const [image, setImage] = useState(null);
    const [visi, setVisi] = useState("");
    const [misi, setMisi] = useState("");
    const [office, setOffice] = useState([]);
    const [alamatfooter, setAlamatfooter] = useState("");
    const [emailfooter, setEmailfooter] = useState("");
    const [telpfooter, setTelpfooter] = useState("");
    const [whatsappfooter, setWhatsappfooter] = useState("");
    const [load, setLoad] = useState(true);
    const [toggle, setToggle] = useState(false);

    const getProfil = async () => {
           const get = await Axios.put("http://192.168.6.235:3001/getprofil", {id: 1})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setProfil(val.profildesc);
                })}
            });
            setLoad(false);
    }
    const getVisi = async () => {
        Axios.put("http://192.168.6.235:3001/getprofil", {id: 2})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setVisi(val.profildesc);
                })}
            })
    }
    const getMisi = async () => {
        Axios.put("http://192.168.6.235:3001/getprofil", {id: 3})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setMisi(val.profildesc);
                })}
            })
    }

    const getImage = async (id) => {
        let get;
        get = await Axios.put("http://192.168.6.235:3001/getprofil", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    let url = "http://192.168.6.235:3001/"+val.gambar_profil
                    setImage(url);
                })}
            })
        getToggle();
    }


    const getToggle = () => {
        setToggle(!toggle);
        if(toggle === false) setImage(null);
    }

    const getOffice = async () => {
        Axios.get("http://192.168.6.235:3001/getoffice").then((response) => {
            setOffice(response.data);
        })
    }

    const getFooter = async () => {
        Axios.get("http://192.168.6.235:3001/getfooter")
        .then((response) => {
            const dataf = response.data;
            {dataf.map((val) => {
                setAlamatfooter(val.jalan_kantor);
                setEmailfooter(val.email);
                setTelpfooter(val.no_telp);
                setWhatsappfooter(val.whatsapp);
            })}
        })
    }

const editorRef = useRef(null);
    const log = () => {
    if (editorRef.current) {
        console.log(editorRef.current.getContent());
    }
  };
    
    
    useEffect(() => {
        // console.log(isSuccess);
        dispatch(getMe());
    }, [dispatch]);
    

    useEffect(()=> {
        // console.log(isError + " " + isSuccess);
        if(isError){
            navigate("/")
        }
        // if(width < 930) setNavbar(false);
        getProfil("");
        // console.log(profil);
        getVisi("");
        // console.log(visi);
        getMisi("");
        // console.log(misi);
        getOffice([]);
        // console.log(office);
        getFooter([]);
    }, [isError]  ,[], [])



    const [isAddOOpen, setIsAddOOpen] = useState(false);
    const [isEditOOpen, setIsEditOOpen] = useState(false);
    const [chidoffice, setChidoffice] = useState("");
    const [chkotaoffice, setChkotaoffice] = useState("");
    const [chjalanoffice, setChjalanoffice] = useState("");
    const [chlatoffice, setChlatoffice] = useState("");
    const [chlongoffice, setChlongoffice] = useState("");


    const toggleAddOPopup = () => {
        setIsAddOOpen(!isAddOOpen);
        if(isAddOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const toggleEditOPopup = () => {
        setIsEditOOpen(!isEditOOpen);
        if(isEditOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const getOfficeId = (id) => {
        Axios.put("http://192.168.6.235:3001/getofficebyid", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChidoffice(val.ID_OFFICE);
                    setChkotaoffice(val.kota);
                    // console.log(chkotaoffice);
                    setChjalanoffice(val.jalan);
                    // console.log(chjalanoffice);
                    setChlatoffice(val.latitude);
                    setChlongoffice(val.longitude);
                    // console.log("jalan")
                })}
            })
        
            toggleEditOPopup();
    }


    const handleProfilChange = (html) => {
        window.onbeforeunload = () => "unsaved";
        setProfil(html);
        const btn = document.getElementById('profilbtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const handleVisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setVisi(event.target.value);
        const btn = document.getElementById('visibtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const handleMisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setMisi(event.target.value);
        const btn = document.getElementById('misibtn');
        btn.style="color: white; background-color: green; display: flex"
    }

    const updateProfil = async() => {
        setProgressBarVisibility(true)
        const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: profil, id: 1})
        setProfil(profil);
        const btn = document.getElementById('profilbtn');
        btn.style="display: none"
        setProgressBarVisibility(false)
    };

    const updateVisi = async() => {
        setProgressBarVisibility(true)
        const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: visi, id: 2})
        setVisi(visi);
        const btn = document.getElementById('visibtn');
        btn.style="display: none"
        setProgressBarVisibility(false)
    };

    const updateMisi = async() => {
        setProgressBarVisibility(true)
        const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {profildesc: misi, id: 3})
        setMisi(misi)
        const btn = document.getElementById('misibtn');
        btn.style="display: none"
        setProgressBarVisibility(false)
    };

    const updateOffice = async(id, e) => {
        e.preventDefault();
        setProgressBarVisibility(true);
        const update = await Axios.put("http://192.168.6.235:3001/updateoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice ,id : id})
        // getOffice();
        window.location.reload();
};

const deleteOffice = async(id) => {
    // console.log("hai");
    setProgressBarVisibility(true)
    const deleted = await Axios.put("http://192.168.6.235:3001/deleteoffice", {id: id});
    window.location.reload();
}

const addOffice = async(e) => {
    e.preventDefault();
    setProgressBarVisibility(true)
    // console.log("hai");
    const add = await Axios.post("http://192.168.6.235:3001/addoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice})
    setProgressBarVisibility(false)
        // getKategori();
        window.location.reload();
}

const updateFooter = async(e) => {
    e.preventDefault();
    setProgressBarVisibility(true)
    // console.log("hai")
    const update = await Axios.put("http://192.168.6.235:3001/updatefooter", {alamat: alamatfooter, email: emailfooter, telp: telpfooter, wa: whatsappfooter ,id : 1})
    setProgressBarVisibility(false)
};

const [isdelete, setIsdelete] = useState(false);

    const toggleDelete = () => {
    setIsdelete(!isdelete);
    if(isdelete === false){
        setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
    }
}

    const getdataDelete = (id) => {
       Axios.put("http://192.168.6.235:3001/getofficebyid", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChidoffice(val.ID_OFFICE);
                    setChkotaoffice(val.kota);
                    // console.log(chkotaoffice);
                    setChjalanoffice(val.jalan);
                    // console.log(chjalanoffice);
                    setChlatoffice(val.latitude);
                    setChlongoffice(val.longitude);
                    // console.log("jalan")
                })}
            })
        toggleDelete();
    }

    const [showProgressBar, setProgressBarVisibility] = useState(false);

    const [text, setText] = useState('');

    const handletext = (html) => {
        setText(html);
        console.log(html)
    }




return(
        <div className="editor">
            {load ? <Loader/> : 
            <>
            <Navbar/>
            {showProgressBar && <>
                <Loader/>
            </>}

            <div className="edit-container" id="editor">
                    <div className="profil-container" id="profil">
                        <div className="row">
                            <h1 className="Profil">Profil</h1>
                            <h1 className="button" onClick={() => updateProfil()}  id='profilbtn'>save</h1>
                            {/* <h1 className="button" onClick={() => getImage(1)} style={{display: 'flex'}}>edit gambar</h1> */}
                        </div>
                        <form>
                            <ReactQuill style={{width: '95%'}} theme="snow" value={profil} onChange={handleProfilChange}/>
                        {/* <Editor
                            onInit={(evt, editor) => editorRef.current = editor}
                            initialValue="<p>This is the initial content of the editor.</p>"
                            init={{
                            height: 500,
                            menubar: false,
                            plugins: [
                            'a11ychecker','advlist','advcode','advtable','autolink','checklist','export',
                            'lists','link','image','charmap','preview','anchor','searchreplace','visualblocks',
                            'powerpaste','fullscreen','formatpainter','insertdatetime','media','table','help','wordcount'
                            ],
                            toolbar: 'undo redo | casechange blocks | bold italic backcolor | ' +
