import Axios from "axios";
import React, { useEffect, useState } from "react";
import './popup.css'
import {Progress} from "reactstrap";

const Sectionimage = props => {
    const [data, setdata] = useState([]);
    const [file, setFile] = useState(null);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [chnamadata, setChnamadata] = useState("");
    const [chgambardata, setChgambardata] = useState("");
    const [chiddata, setChiddata] = useState("");
    const [nfound, setNfound] = useState(false);
    const [search, setSearch] = useState("");
    const url = "http://192.168.6.235:3001/";

    const getdata = async (id) => {
        let tek2 = "%"+id+"%";
        console.log(tek2);
        Axios.put(url+props.getdb, {nama: tek2})
        .then((response) => {
            if(response.data.length == 0) setNfound(true);
            else setNfound(false);
            setdata(response.data);
        })
    };

    function handleChangeSearchK (event) {
        event.preventDefault();
        console.log(search);
        getdata(search);
    }

    useEffect(() => {
        console.log(url);
        getdata([]);
    }, [])

    const toggleEditKPopup = () => {
        setIsEditOpen(!isEditOpen);
        if(isEditOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
            setIschange(false);
            setFile(null);
        }
    }

    const toggleAddKPopup = () => {
        setIsAddOpen(!isAddOpen);
        if(isAddOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
            setFile(null);
        }
    }

    const getdataId = (id) => {
        Axios.put(url+ props.getdbId, {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChiddata(val[props.id]);
                    console.log(chiddata);
                    setChnamadata(val[props.nama]);
                    console.log(chnamadata);
                    setChgambardata(val[props.gambar]);
                    console.log("jalan")
                })}
            })
       
        toggleEditKPopup();
    }

    const handleNamadataChange = (event) => {
        setChnamadata(event.target.value);
        console.log(chnamadata);
    }

    const [ischange, setIschange] = useState(false);

    const handleGambardataChange = async (e) => {
        setIschange(true);
        setFile(e.target.files[0]);
        const base64K = await convertToBase64(e.target.files[0]);
        setChgambardata(base64K);
        console.log(base64K);
        // const imageu = document.getElementById("imageupdate");
        // const formData = new FormData()
        // formData.append("name", chnamadata);
        // formData.append("image", file);
        // const data = Object.fromEntries(formData);
        // console.log(formData);
        // console.log(data);
    }

    // const cropImageNow = () => {
    //     const canvas = document.createElement('canvas');
    //     const scaleX = image.naturalWidth / image.width;
    //     const scaleY = image.naturalHeight / image.height;
    //     canvas.width = crop.width;
    //     canvas.height = crop.height;
    //     const ctx = canvas.getContext('2d');
      
    //     const pixelRatio = window.devicePixelRatio;
    //     canvas.width = crop.width * pixelRatio;
    //     canvas.height = crop.height * pixelRatio;
    //     ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
    //     ctx.imageSmoothingQuality = 'high';
      
    //     ctx.drawImage(
    //       image,
    //       crop.x * scaleX,
    //       crop.y * scaleY,
    //       crop.width * scaleX,
    //       crop.height * scaleY,
    //       0,
    //       0,
    //       crop.width,
    //       crop.height,
    //     );
          
    //     // Converting to base64
    //     const base64Image = canvas.toDataURL('image/jpeg');
    //     setOutput(base64Image);
    //   };

    const updatedata = (id, e) => {
        e.preventDefault();
        setProgressBarVisibility(true);
        console.log("hai");
        const image = document.getElementById("image");
        const formData = new FormData()
        formData.append("id", id);
        formData.append("name", chnamadata);
        formData.append("image", file);
        const data = Object.fromEntries(formData);
        console.log(formData);
        console.log(data);
        // Axios.put("http://192.168.6.235:3001/" + props.update, {namadata: chnamadata, gambardata: chgambardata, id : id})
        Axios({
            method: 'post',
            url: url+ props.update,
            data: formData,
            headers: { "Content-Type": "multipart/form-data" },
            onUploadProgress: progress => {
                const { total, loaded } = progress;
                const totalSizeInMB = total / 1000000;
                const loadedSizeInMB = loaded / 1000000;
                const uploadPercentage = (loadedSizeInMB / totalSizeInMB) * 100;
                setUploadPercentage(uploadPercentage.toFixed(2));
                console.log("total size in MB ==> ", totalSizeInMB);
                console.log("uploaded size in MB ==> ", loadedSizeInMB);
              }
          })
        // window.location.reload();
        // toggleEditKPopup();
};
const [uploadPercentage, setUploadPercentage] = useState(0);
  const [showProgressBar, setProgressBarVisibility] = useState(false);

const adddata = (e) => {
    e.preventDefault();
    console.log("hai");
    const image = document.getElementById("image");
    const formData = new FormData()
    formData.append("name", chnamadata);
    formData.append("image", image.files[0]);
    Axios({
        method: 'post',
        url: url+ props.add,
        data: formData,
        headers: { "Content-Type": "multipart/form-data" },
        onUploadProgress: progress => {
            const { total, loaded } = progress;
            const totalSizeInMB = total / 1000000;
            const loadedSizeInMB = loaded / 1000000;
            const uploadPercentage = (loadedSizeInMB / totalSizeInMB) * 100;
            setUploadPercentage(uploadPercentage.toFixed(2));
            console.log("total size in MB ==> ", totalSizeInMB);
            console.log("uploaded size in MB ==> ", loadedSizeInMB);
          }
      })
    console.log("jalan");
}

const deletedata = (id) => {
    console.log("hai");
    Axios.put("http://192.168.6.235:3001/" + props.delete, {id: id});
    window.location.reload();
}

const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);
      };
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

    return(
        <div className="visimisi-container" id={props.title}>
                        <div className="row">
                        <h1 className="profil">{props.title}</h1>
                        <form onSubmit={(event) => handleChangeSearchK(event)}>
                            <input type='text' className="search" placeholder={"search "+props.title} value={search} onChange={(event) => setSearch(event.target.value)}></input>
                        </form>
                        <h1 className="button add" onClick={() => toggleAddKPopup()} id='misibtn'>tambah</h1>
                        </div>
                        {isAddOpen && <>
                            <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleAddKPopup}>x</span>
                                                <h1>Tambah {props.title}</h1>
                                                <form onSubmit={(event) => adddata(event)} encType="multipart/form-data">
                                                    <div className="data-img-check">
                                                         <img src={chgambardata} style={{width: 'auto'}}/>
                                                    </div>
                                                    {showProgressBar ? (
                                                        <>
                                                        <div className="text-center">
                                                            {parseInt(uploadPercentage) !== 100
                                                            ? `Upload percentage - ${uploadPercentage}`
                                                            : "File successfully uploaded"}
                                                        </div>
                                                        <Progress
                                                            animated={parseInt(uploadPercentage) !== 100}
                                                            color="success"
                                                            value={uploadPercentage}
                                                        />
                                                        </>
                                                    ) : null}
                                                    <input required type='text' placeholder={"nama " + props.title} value={chnamadata} onChange={handleNamadataChange}></input>
                                                    <input required type='file' accept="image/*" name="image" id="image" onChange={handleGambardataChange}></input>
                                                    {/* <textarea required rows={5} placeholder="link gambar..." value={chgambardata} onChange={handleGambardataChange}></textarea> */}
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                        </>}
                        <div className="container-row" id="data">
                            {nfound && <>
                                <h1 className="not-found">data not found</h1>
                            </>}
                            {data.map((val) => {
                                return(
                                <div className="edit-data-container">
                                    <div className="data-img-check section">
                                        <img src={url + val[props.gambar]} style={{maxWidth: "20vw"}}/>
                                    </div>
                                    <div className="col">
                                        <h1>{val[props.nama]}</h1>
                                        <h1 className="button edit" onClick={() => getdataId(val[props.id])}>edit</h1>
                                        <h1 className="button delete" onClick={() => deletedata(val[props.id])}>delete</h1>
                                    </div>
                                    {isEditOpen && <>
                                        <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleEditKPopup}>x</span>
                                                <h1>Edit data</h1>
                                                <form onSubmit={(event) => updatedata(chiddata, event)}>
                                                    <div className="data-img-check">
                                                        <img src={ischange ? chgambardata : url + chgambardata} style={{maxWidth: "20vw"}}/>
                                                    </div>
                                                    <input required type='text' placeholder="nama data" value={chnamadata} onChange={handleNamadataChange}></input>
                                                    <input type='file' accept="image/*" id="imageupdate" name="imageupdate" onChange={handleGambardataChange}></input>
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                                    </>}
                                </div>
                                );
                            })}
                            
                        </div>
                    </div>
    )

}

export default Sectionimage;
