import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import './edit.css'
import './editProfil.css'
import './editKategori.css'
import './editOffice.css'
// import axios, { Axios } from "axios";
import Axios from "axios";
import handleEdit from './handleedit';


export default function Edit () {

    const [profil, setProfil] = useState([]);
    const [kategori, setKategori] = useState([]);
    const [office, setOffice] = useState([]);
    const [mitra, setMitra] = useState([]);
    const [partner, setPartner] = useState([]);
    const [footer, setFooter] = useState([]);

    const [newProfil, setNewProfil] = useState("");
    const [newNamaKategori, setNewNamaKategori] = useState("");
    const [newLinkKategori, setNewLinkKategori] = useState("");
    const [newKota, setNewKota] = useState("");

    const getDataProfil = async () => {
        Axios.get("http://localhost:3001/getprofil").then((response) => {
            setProfil(response.data);
        })
    }

    const getKategori = async () => {
        Axios.get("http://localhost:3001/getkategori").then((response) => {
            setKategori(response.data);
        })
    }

    const getOffice = async () => {
        Axios.get("http://localhost:3001/getoffice").then((response) => {
            setOffice(response.data);
        })
    }

    const getFooter = async () => {
        Axios.get("http://localhost:3001/getfooter").then((response) => {
            setFooter(response.data);
        })
    }


    const buttonChange = () => {
        const btn = document.getElementById(``)
    }
    
    

    useEffect(()=> {
        getDataProfil([]);
        console.log(profil);
        getKategori([]);
        console.log(kategori);
        getOffice([]);
        console.log(office);
        getFooter([]);
        console.log(footer);
        // if(profil.length<=0) getDataProfil([])\
        // api.get("/detail").then((res) => {
        //     setDetail(res.data);
        // })
        // // console.log(detail)
        // api.get("/footer").then((res) => {
        //     setFooter(res.data);
        // })
        // console.log(footer)
    }, [], [], [], [])

    // const [dataProfil, setdataProfil] = useState();
    // const [dataKategori, setdataKategori] = useState();
    // const [dataOffice, setdataOffice] = useState();
    // const [dataMitra, setdataMitra] = useState();
    // const [dataPartner, setdataPartner] = useState();

    // const consData= async() => {
    //     console.log(Data);
    //     {Data && Data.map(dataAll => {
    //         console.log(dataAll.profil)
    //         console.log(dataAll.detail)

    //         {dataAll.profil.map(Profil => {
    //             setdataProfil(Profil);
    //             console.log(dataProfil);
    //             console.log(dataProfil.profildesc)
    //         })}

    //     })}
    // }

    // useEffect(() => {
    //     consData()
    // })

    
    const [textareaheight, setTextareaheight] = useState(`257px`); 
    const [textareaheight2, setTextareaheight2] = useState(1); 
    const [textareaheight3, setTextareaheight3] = useState(1); 
    const [textareaheight4, setTextareaheight4] = useState(1); 
    const [textareaheight5, setTextareaheight5] = useState(1); 
  
    // function handleChange() { 
    //     setTextareaheight(`100px`);
    //     const height = document.getElementById('profil').scrollHeight;
    //     console.log(`${height}px`);
    //     setTextareaheight(`${height}px`)
    // } 

    function handleChange2(event) { 
        const height = event.target.scrollHeight;
        setTextareaheight2(height);
    } function handleChange3(event) { 
        const height = event.target.scrollHeight;
        setTextareaheight3(height/15);
    } function handleChange4(event) { 
        const height = event.target.scrollHeight;
        setTextareaheight4(height/15);
    } function handleChange5(event) { 
        const height = event.target.scrollHeight;
        setTextareaheight5(height/15);
    } 

    const DragDropFiles = () => {
        const [files, setFiles] = useState(null);
        const inputRef = useRef();
      
        const handleDragOver = (event) => {
          event.preventDefault();
        };
      
        const handleDrop = (event) => {
          event.preventDefault();
          setFiles(event.dataTransfer.files)
        };
        
        // send files to the server // learn from my other video
        const handleUpload = () => {
          const formData = new FormData();
          formData.append("Files", files);
          console.log(formData.getAll(files))
        //   fetch(
        //     "link", {
        //       method: "POST",
        //       body: formData
        //     }  
        //   )
        };
      
        if (files) return (
          <div className="dropzone">
              <ul>
                  {Array.from(files).map((file, idx) => <li key={idx}>{file.name}</li> )}
              </ul>
              <div className="actions">
                  <button onClick={() => setFiles(null)}>Cancel</button>
                  <button onClick={handleUpload}>Upload</button>
              </div>
          </div>
        )
      
        return (
          <>
              <div 
                  className="dropzone"
                  onDragOver={handleDragOver}
                  onDrop={handleDrop}
              >
                <h1>Drag and Drop Files to Upload</h1>
                <h1>Or</h1>
                <input 
                  type="file"
                  multiple
                  onChange={(event) => setFiles(event.target.files)}
                  hidden
                  accept="image/png, image/jpeg"
                  ref={inputRef}
                />
                <button onClick={() => inputRef.current.click()}>Select Files</button>
              </div>
          </>
        );
      };

      const [formProfil, setformProfil] = useState({profildesc: "halo", visidesc: "visi", misidesc: "misi"});

    function handleProfil(e){
        let newProfil = {...formProfil};
        newProfil[e.target.name] = e.target.value;
        setformProfil(newProfil);
    }

    // function handleSubmit(e) {
    //     e.preventDefault();
    //     let dataP = [ ...profil];
    //     let dataD = [ ...detail];
    //     let dataF = [ ...footer];

    //     api
    //         .put("/profil", {
    //             profildesc: formProfil.profildesc,
    //             visidesc: formProfil.visidesc,
    //             misidesc: formProfil.misidesc
    //         })
    //         .then(() => {
    //             alert("Updated. Check Website");
    //         })
    // }

    const updateProfil = (id) => {
        Axios.put("http://localhost:3001/updateprofil", {profildesc: newProfil, id: id})
        .then((response) => {
            setProfil(
                profil.map((val) => {
                    return val.id == id
                    ? {
                        profildesc : newProfil
                    } : val;
                })
            )
        })
        alert("Data berhasil diupdate")
    };

    const updateKategori = (id) => {
        
        Axios.put("http://localhost:3001/updatekategori", {namaKategori: newNamaKategori, gambarKategori : newLinkKategori, id: id})
        .then((response) => {
            setKategori(
                kategori.map((val) => {
                    if (val.id_kategori == id){
                        if(newNamaKategori == "") newNamaKategori = val.nama_kategori;
                        if(newLinkKategori == "")  newLinkKategori = val.gambar_kategori;
                        return val.id_kategori == id
                    ? {
                        nama_kategori : newNamaKategori,
                        gambar_kategori : newLinkKategori
                    } : val;
                    } 
                    // return val.id_kategori == id
                    // ? {
                    //     nama_kategori : newNamaKategori,
                    //     gambar_kategori : newLinkKategori
                    // } : val;
                    
                })
            )
        })
        setNewNamaKategori("")
    };

    return(
        <div className="editor">
            <div className="navbar-left">
                <div className="navbar-profile-img">
                    <div className="profile-img"/>
                </div>
                <div className="list-container">
                    <h1 className="list-edit">Profil</h1>
                    <h1 className="list-edit">Kategori Produk</h1>
                    <h1 className="list-edit">Office</h1>
                    <h1 className="list-edit">Mitra</h1>
                    <h1 className="list-edit">Partner</h1>
                    <h1 className="list-edit">Kontak</h1>
                </div>
            </div>

            <div className="edit-container">
                {profil.map((val, key) => {
                    return(
                    <div className="profil-container">
                        <div className="row">
                            <h1 className="Profil">{val.id}</h1>
                            <h1 className="button" onClick={() => updateProfil(val.id)} id={val.id}>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} placeholder={val.profildesc} defaultValue={val.profildesc} onChange={(event) => {setNewProfil(event.target.value)}}></textarea>
                        </form>
                    </div>
                    );
                })}
                {/* <div className="profil-container">
                    <h1 className="Profil">Profil</h1>
                    <form>
                        <textarea rows={10} id='profil' style={{height: `auto`}} value={formProfil.profildesc} onChange={handleProfil}></textarea>
                    </form>
                </div>

                <div className="visimisi-container">
                    <h1 className="Profil">Visi</h1>
                    <form>
                        <textarea rows={8} onChange={handleProfil} id='visi' value={formProfil.visidesc}></textarea>
                    </form>
                </div>

                <div className="visimisi-container">
                    <h1 className="Profil">Misi</h1>
                    <form>
                        <textarea rows={8} onChange={handleProfil} id='misi' value={formProfil.misidesc}></textarea>
                    </form>
                </div> */}

                <div className="visimisi-container">
                <div className="row">
                        <h1 className="Profil">Kategori</h1>
                        <h1 className="button add">Tambah</h1>
                    </div>
                    <div className="container-row">
                        {kategori.map((val, key) => {
                            return(
                                <div className="edit-kategori-container">
                                    <div className="row">
                                        <form>
                                            <label>Nama Kategori</label>
                                            <input type='text' name='kategori' id="Kategori" placeholder={val.nama_kategori} defaultValue={val.nama_kategori} onChange={(event) => setNewNamaKategori(event.target.value)}/>
                                            <label>Link Gambar</label>
                                            <textarea name="kategoriimg" id="KategoriImg" placeholder={val.gambar_kategori} defaultValue={val.gambar_kategori} onChange={(event) => setNewLinkKategori(event.target.value)}></textarea>
                                            <h1 className="button" onClick={() => updateKategori(val.id_kategori)}>save</h1>
                                        </form>
                                    </div>
                                    <div className="kategori-img-check"/>
                                </div>
                            )
                        })}
                        
                    </div>
                    
                </div>

                <div className="visimisi-container">
                    <div className="row">
                        <h1 className="Profil">Office</h1>
                        <h1 className="button add">Tambah</h1>
                    </div>
                <div className="office-editor-container">
                    {office.map((val, key) => {
                        return(
                            <div className="edit-office-container">
                                <div className="row">
                                    <form>
                                        <label>Kota</label>
                                        <input type='text' name='Kota' id="kota" value={val.kota}/>
                                        <label>Jalan</label>
                                        <input type='text' name='Jalan' id="jalan" value={val.jalan}/>
                                        <div className="row">
                                            <div className="col">
                                                <label>Latitude</label>
                                                <input type='text' name='Latitude' id="latitude" value={val.latitude}/>
                                            </div>
                                            <div className="col">
                                                <label>Longitude</label>
                                                <input type='text' name='Longitude' id="longitude" value={val.longitude}/>
                                            </div>
                                        </div>
                                        <input className="submit-btn" type='submit' value={"save"}></input>
                                    </form>
                                </div>
                            </div>
                        )
                    })}
                    
                </div>
                </div>

                <div className="visimisi-container">
                    <div className="row">
                        <h1 className="Profil">Mitra</h1>
                        <h1 className="button add">Tambah</h1>
                    </div>
                    <div className="edit-kategori-container">
                        <div className="row">
                            <form>
                                <label>Nama Mitra</label>
                                <input type='text' name='mitra' id="Mitra"/>
                                <label>Link Gambar</label>
                                <textarea name="mitraimg" id="mitraImg"></textarea>
                            </form>
                        </div>
                        <div className="kategori-img-check"/>
                    </div>
                </div>

                

                <div className="visimisi-container">
                <h1 className="Profil">Partner</h1>
                    {/* {DragDropFiles()} */}
                </div>


                <div className="visimisi-container">
                <h1 className="Profil">Kontak</h1>
                <div className="office-editor-container">
                    <div className="edit-office-container">
                        <div className="row">
                            {footer.map((val, key) => {
                                return(
                                    <form>
                                        <label>Alamat Kantor Pusat</label>
                                        <input type='text' name='Alamatkantor' id="alamatkantor" value={val.jalan_kantor}/>
                                        <label>Email</label>
                                        <input type='text' name='Email' id="email" value={val.email}/>
                                        <div className="row">
                                            <div className="col">
                                                <label>No. Telepon</label>
                                                <input type='text' name='Telepon' id="telepon" value={val.no_telp}/>
                                            </div>
                                            <div className="col">
                                                <label>Whatsapp</label>
                                                <input type='text' name='Whatsapp' id="whatsapp" value={val.whatsapp}/>
                                            </div>
                                        </div>
                                        <input className="submit-btn" type='submit' value={"save"}></input>
                                    </form>
                                )
                            })}
                            
                        </div>
                    </div>
                </div>
                </div>


            </div>


        </div>
    )
}
