import React, { useEffect, useState } from "react";
import './login.css'
import {useDispatch, useSelector} from "react-redux";
import { useNavigate } from "react-router-dom";
import { LoginUser, reset } from "./auth";


export default function Login () {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const dispatch = useDispatch();
    const navigate = useNavigate();
    const {user, isError, isSuccess, isLoading, message} = useSelector(
        (state) => state.auth
    );

    useEffect(() => {
        if(user || isSuccess) {
            navigate("/edit");
        }
        dispatch(reset());
    }, [user, isSuccess, dispatch, navigate]);

    const Auth = (e) => {
        e.preventDefault();
        dispatch(LoginUser({ username, password}));
    }

    return(
        <div className="body">
        <div className="login-container">
            <h1>Login Admin</h1>
            <form onSubmit={Auth}>
                <input id="Uname" name="username" type='text' placeholder='username' value={username} onChange={(event) => setUsername(event.target.value)}/>
                <input id="Pass" name="password" type='password' placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)}/>
                <input type="submit" className="submit" value='Login'/>
            </form>
        </div>
        
            
        </div>
    )
}
