import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import './edit.css'
import './editProfil.css'
import './editKategori.css'
import './editOffice.css'
// import axios, { Axios } from "axios";
import Axios from "axios";
import Sectionimage from "./sectionimage";
import {useSelector, useDispatch} from "react-redux";
import { useNavigate } from "react-router-dom";
import { getMe, LogOut, reset } from "./auth";



export default function Edit () {

    const {user, isError} = useSelector(
        (state) => state.auth
    );
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const [profil, setProfil] = useState("");
    const [visi, setVisi] = useState("");
    const [misi, setMisi] = useState("");
    const [office, setOffice] = useState([]);
    const [alamatfooter, setAlamatfooter] = useState("");
    const [emailfooter, setEmailfooter] = useState("");
    const [telpfooter, setTelpfooter] = useState("");
    const [whatsappfooter, setWhatsappfooter] = useState("");

    const getProfil = async () => {
            Axios.put("http://localhost:3001/getprofil", {id: 1})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setProfil(val.profildesc);
                })}
            })
    }
    const getVisi = async () => {
        Axios.put("http://localhost:3001/getprofil", {id: 2})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setVisi(val.profildesc);
                })}
            })
    }
    const getMisi = async () => {
        Axios.put("http://localhost:3001/getprofil", {id: 3})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setMisi(val.profildesc);
                })}
            })
    }

    const getOffice = async () => {
        Axios.get("http://localhost:3001/getoffice").then((response) => {
            setOffice(response.data);
        })
    }

    const getFooter = async () => {
        Axios.get("http://localhost:3001/getfooter")
        .then((response) => {
            const dataf = response.data;
            {dataf.map((val) => {
                setAlamatfooter(val.jalan_kantor);
                setEmailfooter(val.email);
                setTelpfooter(val.no_telp);
                setWhatsappfooter(val.whatsapp);
            })}
        })
    }

    const [isLogoutOpen, SetIsLogoutOpen] = useState(false);
    const [navbar, setNavbar] = useState(false);

    const toggleNavbar = () => {
        console.log(navbar)
        setNavbar(!navbar);
    }

    // window.addEventListener('scroll', toggleNavbar());


    // window.addEventListener("scroll", closeNavbar());

    // function closeNavbar () {
    //     if(navbar === true) toggleNavbar();
    //     else setNavbar(false);
    // }

    const toggleLogout = () => {
        SetIsLogoutOpen(!isLogoutOpen);
    }

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };
    
    useEffect(() => {
        dispatch(getMe());
    }, [dispatch]);
    

    useEffect(()=> {
        // console.log(isError);
        if(isError){
            navigate("/")
        }
        getProfil("");
        console.log(profil);
        getVisi("");
        console.log(visi);
        getMisi("");
        console.log(misi);
        getOffice([]);
        console.log(office);
        getFooter([]);
    }, [isError]  ,[], [])



    const [isAddOOpen, setIsAddOOpen] = useState(false);
    const [isEditOOpen, setIsEditOOpen] = useState(false);
    const [chidoffice, setChidoffice] = useState("");
    const [chkotaoffice, setChkotaoffice] = useState("");
    const [chjalanoffice, setChjalanoffice] = useState("");
    const [chlatoffice, setChlatoffice] = useState("");
    const [chlongoffice, setChlongoffice] = useState("");


    const toggleAddOPopup = () => {
        setIsAddOOpen(!isAddOOpen);
        if(isAddOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const toggleEditOPopup = () => {
        setIsEditOOpen(!isEditOOpen);
        if(isEditOOpen === false){
            setChkotaoffice("");
            setChjalanoffice("");
            setChlatoffice("");
            setChlongoffice("");
        }
    }

    const getOfficeId = (id) => {
        Axios.put("http://localhost:3001/getofficebyid", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChidoffice(val.ID_OFFICE);
                    setChkotaoffice(val.kota);
                    console.log(chkotaoffice);
                    setChjalanoffice(val.jalan);
                    console.log(chjalanoffice);
                    setChlatoffice(val.latitude);
                    setChlongoffice(val.longitude);
                    console.log("jalan")
                })}
            })
        
            toggleEditOPopup();
    }


    const handleProfilChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setProfil(event.target.value);
        const btn = document.getElementById('profilbtn');
        btn.style="color: white; background-color: green"
    }

    const handleVisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setVisi(event.target.value);
        const btn = document.getElementById('visibtn');
        btn.style="color: white; background-color: green"
    }

    const handleMisiChange = (event) => {
        window.onbeforeunload = () => "unsaved";
        setMisi(event.target.value);
        const btn = document.getElementById('misibtn');
        btn.style="color: white; background-color: green"
    }

    const updateProfil = () => {
        Axios.put("http://localhost:3001/updateprofil", {profildesc: profil, id: 1})
        setProfil(profil);
        alert("Data berhasil diupdate");
        const btn = document.getElementById('profilbtn');
        btn.style="color: black; background-color: ghostwhite"
    };

    const updateVisi = (id) => {
        Axios.put("http://localhost:3001/updateprofil", {profildesc: visi, id: 2})
        setVisi(visi);
        alert("Data berhasil diupdate")
        const btn = document.getElementById('visibtn');
        btn.style="color: black; background-color: ghostwhite"
    };

    const updateMisi = (id) => {
        Axios.put("http://localhost:3001/updateprofil", {profildesc: misi, id: 3})
        setMisi(misi)
        alert("Data berhasil diupdate")
        const btn = document.getElementById('misibtn');
        btn.style="color: black; background-color: ghostwhite"
    };

    const updateOffice = (id, e) => {
        e.preventDefault();
        console.log("hai")
        Axios.put("http://localhost:3001/updateoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice ,id : id})
        // getOffice();
        window.location.reload();
        toggleEditOPopup();
};

const deleteOffice = (id) => {
    console.log("hai");
    Axios.put("http://localhost:3001/deleteoffice", {id: id});
    window.location.reload();
}

const addOffice = (e) => {
    e.preventDefault();
    console.log("hai");
    Axios.post("http://localhost:3001/addoffice", {kota: chkotaoffice, jalan: chjalanoffice, lat: chlatoffice, long: chlongoffice})
        // getKategori();
        // window.location.reload();
}

const updateFooter = (e) => {
    e.preventDefault();
    console.log("hai")
    Axios.put("http://localhost:3001/updatefooter", {alamat: alamatfooter, email: emailfooter, telp: telpfooter, wa: whatsappfooter ,id : 1})
};




return(
        <div className="editor">
            <div className="bar" onClick={toggleNavbar}>
                <svg className="bar-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512" width={'20px'} fill={'white'}><path d="M0 96C0 78.3 14.3 64 32 64H416c17.7 0 32 14.3 32 32s-14.3 32-32 32H32C14.3 128 0 113.7 0 96zM0 256c0-17.7 14.3-32 32-32H416c17.7 0 32 14.3 32 32s-14.3 32-32 32H32c-17.7 0-32-14.3-32-32zM448 416c0 17.7-14.3 32-32 32H32c-17.7 0-32-14.3-32-32s14.3-32 32-32H416c17.7 0 32 14.3 32 32z"/></svg>
            </div>
            <div className={navbar ? 'navbar-left' : 'navbar-left-closed'} id="navbar-left">
                <div className="navbar-profile-img">
                    <div className="profile-img"/>
                </div>
                <div className="list-container">
                    <a className="list-edit" href="#profil">Profil</a>
                    <a className="list-edit" href="#Kategori">Kategori Produk</a>
                    <a className="list-edit" href="#office">Office</a>
                    <a className="list-edit" href="#Mitra">Mitra</a>
                    <a className="list-edit" href="#Partner">Partner</a>
                    <a className="list-edit" href="#kontak">Kontak</a>
                </div>
                <h1 className="logout" onClick={toggleLogout}>Logout</h1>
            </div>
            {isLogoutOpen && <div className="popup-logout">
                <div className="popup-box">
                    <div className="box">
                        <h1 style={{color: 'black'}}>Apakah Anda yakin untuk keluar?</h1>
                        <div className="row">
                        <h1 className="button" onClick={() => logout()}  id='profilbtn'>Logout</h1>
                        <h1 className="button" id='cancel-logout' onClick={() => toggleLogout()}>cancel</h1>
                        </div>
                </div>
            </div>
            </div>}

            <div className="edit-container" id="editor">
                    <div className="profil-container" id="profil">
                        <div className="row">
                            <h1 className="Profil">Profil</h1>
                            <h1 className="button" onClick={() => updateProfil()}  id='profilbtn'>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} value={profil}  onChange={handleProfilChange}></textarea>
                        </form>
                    </div>

                    <div className="profil-container" id="visi">
                        <div className="row">
                            <h1 className="Profil">Visi</h1>
                            <h1 className="button" onClick={() => updateVisi()} id='visibtn'>save</h1>
                        </div>
                        <form>
                            <textarea rows={10} id='profil' style={{height: `auto`}} value={visi}  onChange={handleVisiChange}></textarea>
                        </form>
                    </div>

                    <div className="profil-container" id="misi">
                        <div className="row">
                            <h1 className="Profil">Misi</h1>
                            <h1 className="button" onClick={() => updateMisi()} id='misibtn'>save</h1>
                        </div>
                        <form>
