    const get = await Axios.put("http://192.168.6.235:3001/getprofil", {
      id: 1,
    }).then((response) => {
      const data = response.data;
      {
        data.map((val) => {
          setProfil(val.profildesc);
        });
      }
    });
    setLoad(false);
  };
  const getVisi = async () => {
