      (response) => {
        const data = response.data;
        {
          data.map((val) => {
            setVisi(val.profildesc);
          });
        }
      }
    );
  };
  const getMisi = async () => {
    Axios.put("http://192.168.6.235:3001/getprofil", { id: 3 }).then(
      (response) => {
        const data = response.data;
        {
          data.map((val) => {
            setMisi(val.profildesc);
          });
        }
      }
    );
  };

  const getImage = async (id) => {
    // setLoad(true);
    console.log(id);
    let get;
    get = await Axios.put("http://192.168.6.235:3001/getprofil", {
      id: id,
    }).then((response) => {
      const data = response.data;
      {
        data.map((val) => {
          let url = "http://192.168.6.235:3001/" + val.gambar_profil;
          console.log(url);
          setImage(url);
        });
      }
    });
    // setLoad(false);
    if (id === 2) if (toggleV === false) getToggleV();
    if (id === 3) {
      if (toggleM === false) {
        console.log("jalan");
        getToggleM();
      }
    }
  };

  const getToggleV = () => {
    setToggleV(!toggleV);
    // if (toggle === false) setImage(null);
  };

  const getToggleM = () => {
    setToggleM(!toggleM);
    // if (toggle === false) setImage(null);
  };

  const handleLogofChange = async (id, nama, e) => {
    e.preventDefault();
    setProgressBarVisibility(true);
    try {
      console.log(e.target.files[0]);
      const formData = new FormData();
      formData.append("id", id);
      formData.append("name", nama);
      formData.append("image", e.target.files[0]);
      const update = await updateData(formData);
      setProgressBarVisibility(false);
      if (showProgressBar === false) {
        getImage(id);
      }
    } catch {
      setProgressBarVisibility(false);
    }
  };

  const updateData = async (data) => {
    const updated = await Axios({
      method: "post",
      url: "http://192.168.6.235:3001/updateimageprofil",
      data: data,
      headers: { "Content-Type": "multipart/form-data" },
    });
  };

  const getFooter = async () => {
    Axios.get("http://192.168.6.235:3001/getfooter").then((response) => {
      const dataf = response.data;
      {
        dataf.map((val) => {
          setAlamatfooter(val.jalan_kantor);
          setEmailfooter(val.email);
          setTelpfooter(val.no_telp);
          setWhatsappfooter(val.whatsapp);
        });
      }
    });
  };

  const editorRef = useRef(null);
  const log = () => {
    if (editorRef.current) {
      console.log(editorRef.current.getContent());
    }
  };

  useEffect(() => {
    // console.log(isSuccess);
    dispatch(getMe());
  }, [dispatch]);

  useEffect(
    () => {
      // console.log(isError + " " + isSuccess);
      if (isError) {
        navigate("/");
      }
      // if(width < 930) setNavbar(false);
      getProfil("");
      // console.log(profil);
      getVisi("");
      // console.log(visi);
      getMisi("");
      // console.log(misi);
      // console.log(office);
      getFooter([]);
    },
    [isError],
    [],
    []
  );

  const handleProfilChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setProfil(html);
    const btn = document.getElementById("profilbtn");
    btn.style = "color: white; background-color: green; display: flex";
  };

  const handleVisiChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setVisi(html);
    const btn = document.getElementById("visibtn");
    btn.style = "color: white; background-color: green; display: flex";
    const btn2 = document.getElementById("editvisi");
    btn2.style = "display: none";
  };

  const handleMisiChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setMisi(html);
    const btn = document.getElementById("misibtn");
    btn.style = "color: white; background-color: green; display: flex";
    const btn2 = document.getElementById("editmisi");
    btn2.style = "display: none";
  };

  const updateProfil = async () => {
    setProgressBarVisibility(true);
    const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {
      profildesc: profil,
      id: 1,
    });
    setProfil(profil);
    const btn = document.getElementById("profilbtn");
    btn.style = "display: none";
    setProgressBarVisibility(false);
  };

  const updateVisi = async () => {
    setProgressBarVisibility(true);
    const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {
      profildesc: visi,
      id: 2,
    });
    setVisi(visi);
    const btn = document.getElementById("visibtn");
    btn.style = "display: none";
    const btn2 = document.getElementById("editvisi");
    btn2.style = "display: flex";
    setProgressBarVisibility(false);
  };

  const updateMisi = async () => {
    setProgressBarVisibility(true);
    const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {
      profildesc: misi,
      id: 3,
    });
    setMisi(misi);
    const btn = document.getElementById("misibtn");
    btn.style = "display: none";
    const btn2 = document.getElementById("editmisi");
    btn2.style = "display: flex";
    setProgressBarVisibility(false);
  };

  const updateFooter = async (e) => {
    e.preventDefault();
    setProgressBarVisibility(true);
    // console.log("hai")
    const update = await Axios.put("http://192.168.6.235:3001/updatefooter", {
      alamat: alamatfooter,
      email: emailfooter,
      telp: telpfooter,
      wa: whatsappfooter,
      id: 1,
    });
    setProgressBarVisibility(false);
  };

  const [showProgressBar, setProgressBarVisibility] = useState(false);

  return (
    <div className="editor">
          <div className="edit-container" id="editor">
            {showProgressBar && <Loader />}
            <div className="profil-container" id="profil">
              <div className="row">
                <h1 className="Profil">Profil</h1>
                <h1
                  className="button"
                  onClick={() => updateProfil()}
                  id="profilbtn"
                >
                  save
                </h1>
              </div>
              <form>
                <ReactQuill
                  style={{ width: "95%" }}
                  theme="snow"
                  value={profil}
                  onChange={handleProfilChange}
                />
                {/* <Editor
                            onInit={(evt, editor) => editorRef.current = editor}
                            initialValue="<p>This is the initial content of the editor.</p>"
                            init={{
                            height: 500,
                            menubar: false,
                            plugins: [
                            'a11ychecker','advlist','advcode','advtable','autolink','checklist','export',
                            'lists','link','image','charmap','preview','anchor','searchreplace','visualblocks',
                            'powerpaste','fullscreen','formatpainter','insertdatetime','media','table','help','wordcount'
                            ],
                            toolbar: 'undo redo | casechange blocks | bold italic backcolor | ' +
                            'alignleft aligncenter alignright alignjustify | ' +
                            'bullist numlist checklist outdent indent | removeformat | a11ycheck code table help',
                            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                            }}
                        /> */}
                {/* <textarea rows={10} id='profil' style={{height: `auto`}} value={profil}  onChange={handleProfilChange}></textarea> */}
              </form>
            </div>

            <div className="profil-container" id="visi">
              <div className="row">
                <h1 className="Profil">Visi</h1>
                <h1
                  className="button"
                  onClick={() => updateVisi()}
                  id="visibtn"
                >
                  save
                </h1>
                <h1
                  className="button"
                  onClick={() => getImage(2)}
                  style={{ display: "flex" }}
                  id="editvisi"
                >
                  edit gambar
                </h1>
              </div>
              {toggleV && (
                <div className="popup-box">
                  <div className="box">
                    <span className="close-icon" onClick={getToggleV}>
                      x
                    </span>
                    <h1 style={{ fontWeight: "300" }}>Edit Gambar Visi</h1>
                    <form encType="multipart/form-data">
                      <div className="data-img-check">
                        <img src={image} style={{ maxWidth: "80%" }} />
                      </div>
                      <input
                        required
                        type="file"
                        accept="image/png, image/jpeg, image/jpg"
                        name="image"
                        id="image"
                        onChange={(e) => handleLogofChange(2, "visi", e)}
                      ></input>
                    </form>
                  </div>
                </div>
              )}
              <form>
                <ReactQuill
                  style={{ width: "95%" }}
                  theme="snow"
                  value={visi}
                  onChange={handleVisiChange}
                />
                {/* <textarea rows={10} id='profil' style={{height: `auto`}} value={visi}  onChange={handleVisiChange}></textarea> */}
              </form>
            </div>

            <div className="profil-container" id="misi">
              <div className="row">
                <h1 className="Profil">Misi</h1>
                <h1
                  className="button"
                  onClick={() => updateMisi()}
                  id="misibtn"
                >
                  save
                </h1>
                <h1
                  className="button"
                  onClick={() => getImage(3)}
                  style={{ display: "flex" }}
                  id="editmisi"
                >
                  edit gambar
                </h1>
              </div>
              {toggleM && (
                <div className="popup-box">
                  <div className="box">
                    <span className="close-icon" onClick={getToggleM}>
                      x
                    </span>
                    <h1 style={{ fontWeight: "300" }}>Edit Gambar Misi</h1>
                    <form encType="multipart/form-data">
                      <div className="data-img-check">
                        <img src={image} style={{ maxWidth: "80%" }} />
                      </div>
                      <input
                        required
                        type="file"
                        accept="image/png, image/jpeg, image/jpg"
                        name="image"
                        id="image"
                        onChange={(e) => handleLogofChange(3, "misi", e)}
                      ></input>
                    </form>
                  </div>
                </div>
              )}
              <form>
                <ReactQuill
                  style={{ width: "95%" }}
                  theme="snow"
                  value={misi}
                  onChange={handleMisiChange}
                />

                {/* <textarea rows={10} id='profil' style={{height: `auto`}} value={misi}  onChange={handleMisiChange}></textarea> */}
              </form>
            </div>

            <div className="visimisi-container" id="kontak">
              <h1 className="profil">Kontak</h1>
              <div className="office-editor-container flow">
                <div className="edit-office-container shadow">
                  <form onSubmit={(event) => updateFooter(event)}>
                    <label>Alamat Kantor Pusat</label>
                    <textarea
                      value={alamatfooter}
                      onChange={(event) => setAlamatfooter(event.target.value)}
                    ></textarea>
                    <label>Email</label>
                    <input
                      className="height-auto"
                      type="text"
                      value={emailfooter}
                      onChange={(event) => setEmailfooter(event.target.value)}
                    ></input>
                    <div className="row">
                      <div className="col">
                        <label>No. Telepon</label>
                        <input
                          type="text"
                          value={telpfooter}
                          onChange={(event) =>
                            setTelpfooter(event.target.value)
                          }
                        ></input>
                      </div>
                      <div className="col">
                        <label>Whatsapp</label>
                        <input
                          type="text"
                          value={whatsappfooter}
                          onChange={(event) =>
                            setWhatsappfooter(event.target.value)
                          }
                        ></input>
                      </div>
                    </div>
                    <input
                      type="submit"
                      value="save changes"
                      className="footer-btn height-auto"
                    ></input>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
  );
}
