import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import './edit.css'
import './editProfil.css'
import './editKategori.css'
import './editOffice.css'
// import axios, { Axios } from "axios";
import Axios from "axios";
import Popup from "./popup";
import handleEdit from './handleedit';


export default function Edit () {

    const [Loading, setLoading] = useState(true);
    const [nsaved, setNsaved] = useState(false);

    const [profil, setProfil] = useState("");
    const [visi, setVisi] = useState("");
    const [misi, setMisi] = useState("");
    const [kategori, setKategori] = useState([]);
    const [office, setOffice] = useState([]);
    const [mitra, setMitra] = useState([]);
    const [partner, setPartner] = useState([]);
    const [footer, setFooter] = useState([]);
    const [newNamaKategori, setNewNamaKategori] = useState("");
    const [newLinkKategori, setNewLinkKategori] = useState("");
    const [newKota, setNewKota] = useState("");

    const getProfil = async () => {
            Axios.put("http://localhost:3001/getprofil", {id: 1})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setProfil(val.profildesc);
                })}
            })
    }
    const getVisi = async () => {
        Axios.put("http://localhost:3001/getprofil", {id: 2})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setVisi(val.profildesc);
                })}
            })
    }
    const getMisi = async () => {
        Axios.put("http://localhost:3001/getprofil", {id: 3})
            .then((response) => {
                const data = response.data;
                {data.map((val) => {
                    setMisi(val.profildesc);
                })}
            })
    }

    const getKategori = async () => {
        Axios.get("http://localhost:3001/getkategori").then((response) => {
            setKategori(response.data);
        })
    }

    const getOffice = async () => {
        Axios.get("http://localhost:3001/getoffice").then((response) => {
            setOffice(response.data);
        })
    }

    const getFooter = async () => {
        Axios.get("http://localhost:3001/getfooter").then((response) => {
            setFooter(response.data);
        })
    }


    const buttonChange = () => {
        const btn = document.getElementById(``)
    }
    
    

    useEffect(()=> {
        getProfil("");
        console.log(profil);
        getVisi("");
        console.log(visi);
        getMisi("");
        console.log(misi);
        getKategori([]);
        console.log(kategori);
        getOffice([]);
        console.log(office);
        getFooter([]);
        console.log(footer);
