import Axios from "axios";
import { useEffect, useState } from "react";

export default function Kategori () {
    const [kategori, setKategori] = useState([]);
    const [isEditKOpen, setIsEditKOpen] = useState(false);
    const [isAddKOpen, setIsAddKOpen] = useState(false);
    const [chnamaKategori, setChnamaKategori] = useState("");
    const [chgambarKategori, setChgambarKategori] = useState("");
    const [chidKategori, setChidKategori] = useState("");
    const [load, setLoad] = useState(true);
    const [searchK, setSearchK] = useState("");


    const getKategori = async () => {
        setLoad(true);
        let tek2 = "%"+searchK+"%";
        console.log(tek2);
        Axios.put("http://localhost:3001/getkategorinama", {nama: tek2})
        .then((response) => {
            setKategori(response.data);
        })
        setLoad(false);
    };

    const handleChangeSearchK = (event) => {
        setSearchK(event.target.value);
        
    }

    useEffect(() => {
        getKategori([]);
    }, [])

    const toggleEditKPopup = () => {
        setIsEditKOpen(!isEditKOpen);
        if(isEditKOpen === false){
            setChgambarKategori("");
            setChidKategori("");
            setChnamaKategori("");
        }
    }

    const toggleAddKPopup = () => {
        setIsAddKOpen(!isAddKOpen);
        if(isAddKOpen === false){
            setChgambarKategori("");
            setChidKategori("");
            setChnamaKategori("");
        }
    }

    const getKategoriId = (id) => {
        Axios.put("http://localhost:3001/getKategoriID", {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChidKategori(val.id_kategori);
                    console.log(chidKategori);
                    setChnamaKategori(val.nama_kategori);
                    console.log(chnamaKategori);
                    setChgambarKategori(val.gambar_kategori);
                    console.log("jalan")
                })}
            })
       
        toggleEditKPopup();
    }

    const handleNamaKategoriChange = (event) => {
        setChnamaKategori(event.target.value);
    }

    const handleGambarKategoriChange = async (e) => {
        const fileK = e.target.files[0];
        const base64K = await convertToBase64(fileK);
        setChgambarKategori(base64K);
        
        console.log(chgambarKategori);
    }

    const updateKategori = (id, e) => {
        e.preventDefault();
        console.log("hai")
        Axios.put("http://localhost:3001/updatekategori", {namaKategori: chnamaKategori, gambarKategori: chgambarKategori, id : id})
        // getKategori();
        window.location.reload();
        // document.getElementById('kategori').
        // .then((response) => {
        //     setKategori(
        //         kategori.map((val) => {
        //             return val.id_kategori == id
        //             ? {
        //                 id: val.id_kategori,
        //                 nama_kategori: chnamaKategori,
        //                 gambar_kategori: chgambarKategori
        //             } : val;
        //         })
        //     )
        // })
        toggleEditKPopup();
};

const addKategori = (e) => {
    e.preventDefault();
    console.log("hai");
    Axios.post("http://localhost:3001/addkategori", {namaKategori: chnamaKategori, gambarKategori: chgambarKategori})
        // getKategori();
        window.location.reload();
}

const deleteKategori = (id) => {
    console.log("hai");
    Axios.put("http://localhost:3001/deletekategori", {id: id});
    window.location.reload();
}

const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);
      };
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

if(load === false){
    return(
        <div className="visimisi-container">
                        <div className="row">
                        <h1 className="profil">Kategori</h1>
                        <form>
                            <input type='text' placeholder="search kategori" value={searchK} onChange={(event) => handleChangeSearchK(event.target.value)}></input>
                        </form>
                        <h1 className="button add" onClick={() => toggleAddKPopup()} id='misibtn'>tambah</h1>
                        </div>
                        {isAddKOpen && <>
                            <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleAddKPopup}>x</span>
                                                <h1>Tambah Kategori</h1>
                                                <form onSubmit={(event) => addKategori(event)} encType="multipart/form-data">
                                                    <div className="kategori-img-check">
                                                         <img src={chgambarKategori} style={{maxWidth: "20vw"}}/>
                                                    </div>
                                                    <input required type='text' placeholder="nama kategori" value={chnamaKategori} onChange={handleNamaKategoriChange}></input>
                                                    <input required type='file' accept="image/*" name="gambarKategori" onChange={handleGambarKategoriChange}></input>
                                                    {/* <textarea required rows={5} placeholder="link gambar..." value={chgambarKategori} onChange={handleGambarKategoriChange}></textarea> */}
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                        </>}
                        <div className="container-row" id="kategori">
                            {kategori.map((val) => {
                                return(
                                <div className="edit-kategori-container">
                                    <div className="kategori-img-check section">
                                        <img src={val.gambar_kategori} style={{maxWidth: "20vw"}}/>
                                    </div>
                                    <div className="col">
                                        <h1>{val.nama_kategori}</h1>
                                        <h1 className="button edit" onClick={() => getKategoriId(val.id_kategori)}>edit</h1>
                                        <h1 className="button delete" onClick={() => deleteKategori(val.id_kategori)}>delete</h1>
                                    </div>
                                    {isEditKOpen && <>
                                        <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleEditKPopup}>x</span>
                                                <h1>Edit Kategori</h1>
                                                <form onSubmit={(event) => updateKategori(chidKategori, event)}>
                                                    <div className="kategori-img-check">
                                                        <img src={chgambarKategori} style={{maxWidth: "20vw"}}/>
                                                    </div>
                                                    <input required type='text' placeholder="nama kategori" value={chnamaKategori} onChange={handleNamaKategoriChange}></input>
                                                    <input type='file' onChange={handleGambarKategoriChange}></input>
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                                    </>}
                                </div>
                                );
                            })}
                            
                        </div>
                    </div>
    )
} else if (load === true){
    return(
    <span>loading..</span>
)}

}
