import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { LogOut, reset } from "./auth";
import { HashLink, NavHashLink } from 'react-router-hash-link';
import Axios from "axios";
import './popup.css'
import Loader from "./loader";


export default function Navbar() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [isLogoutOpen, SetIsLogoutOpen] = useState(false);
    const [navbar, setNavbar] = useState(false);
    const [editL, setEditL] = useState(false);
    const [ischange, setIschange] = useState(false);
    const [isfchange, setIsfchange] = useState(false);
    const [dataL, setDataL] = useState([]);
    const [logo, setLogo] = useState(null);
    const [logo64, setLogo64] = useState(null);
    const [load, setLoad] = useState(false);
    const [logof64, setLogof64] = useState(null);
    const [logof, setLogof] = useState(null);
    const [slogo, setSlogo] = useState(null);
    const url = "http://192.168.6.235:3001/";


    function toggleNavbar () {
        console.log("next")
        setNavbar(true);
        console.log(navbar)
    }

    useEffect(() => {
        // if(window.innerWidth < 930 && navbar === true) setNavbar(false);
        if(window.innerWidth >= 930) setNavbar(true);
        if(navbar === true) document.getElementById('editor').addEventListener("click", setNavbarWidth);
        window.addEventListener('resize', fixNavbar);
    }, [])

    const setNavbarWidth = () => {
        const width = window.innerWidth;
        if(navbar === true && width < 930)setNavbar(false);
    }

    const fixNavbar = () => {
        const width = window.innerWidth;
        console.log(width);
        if(width > 929) setNavbar(true);
        else setNavbar(false);
    }

    const toggleEditL = () => {
        setEditL(!editL);
        getDataL();
        if(editL === false){
            setDataL([""]);
        }
    }

    const convertToBase64 = (file) => {
        return new Promise((resolve, reject) => {
          const fileReader = new FileReader();
          fileReader.readAsDataURL(file);
          fileReader.onload = () => {
            resolve(fileReader.result);
          };
          fileReader.onerror = (error) => {
            reject(error);
          };
        });
      };

    const handleLogofChange = async (id, e) => {
        e.preventDefault()
        setLoad(true);
        try{
            // const base64K = await convertToBase64(e.target.files[0]);
            // setLogof64(base64K);
            // console.log(base64K);
            // const set = await setLogof(e.target.files[0]);
            const formDataL = new FormData()
            formDataL.append("id", id);
            formDataL.append("name", "logofooter");
            formDataL.append("image", e.target.files[0]);
            const update = await updateData(formDataL);
            setLoad(false);
            if(load === false) {
                const updated = await Axios.get("http://192.168.6.235:3001/getlogo")
                .then((response) => {
                    setDataL(response.data);
                })
            }
        } catch{
            setLogof64(null);
            setIsfchange(false);
        }
    }

    const [imageload, setImageload] = useState(false);

    useEffect(() => {
        getDataL();
    }, [])

    const getDataL = async () => {
        console.log("logo jalan")
        const get = await Axios.get("http://192.168.6.235:3001/getlogo")
        .then((response) => {
            setDataL(response.data);
        })
        console.log(dataL);
        const get2 = await dataL.slice(0,1).map((val) => {
            setSlogo(val.gambar_logo)
        })
        console.log("terakhhir logo jalan")
        if(slogo != null) setImageload(true);
        // toggleEditL();
    };

    const updateData = async(data) => {
        const updated = await Axios({
            method: 'post',
            url: "http://192.168.6.235:3001/updatelogo",
            data: data,
            headers: { "Content-Type": "multipart/form-data" }
        })
    }

    const updateDataL = (id, e) => {
        e.preventDefault();
        while(ischange === true || isfchange === true){
            if(ischange === true){
                console.log("update jalan")
                const formDataL = new FormData()
                formDataL.append("id", id);
                formDataL.append("name", "logo");
                formDataL.append("image", logo);
                updateData(formDataL);
                setIschange(false);
            } else {
                const formDataLf = new FormData()
                formDataLf.append("id", id);
                formDataLf.append("name", "logofooter")
                formDataLf.append("image", logof);
                updateData(formDataLf);
                setIsfchange(false);
            }
        }
    }

    // if(width < 930) {
    //     window.addEventListener('click', closeNavbar);
    // }

    // // window.addEventListener("scroll", closeNavbar());

    // function closeNavbar () {
    //     if(navbar === true) {
    //         setNavbar(false);
    //     }
    //     else setNavbar(false);
    // }

    const toggleLogout = () => {
        SetIsLogoutOpen(!isLogoutOpen);
    }

    const logout = () => {
        dispatch(LogOut());
        dispatch(reset());
        navigate("/");
    };

    return(
        <>
        {imageload ? 

        <>
        <div className="bar" onClick={toggleNavbar}>
                <svg className="bar-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512" width={'20px'} fill={'white'}><path d="M0 96C0 78.3 14.3 64 32 64H416c17.7 0 32 14.3 32 32s-14.3 32-32 32H32C14.3 128 0 113.7 0 96zM0 256c0-17.7 14.3-32 32-32H416c17.7 0 32 14.3 32 32s-14.3 32-32 32H32c-17.7 0-32-14.3-32-32zM448 416c0 17.7-14.3 32-32 32H32c-17.7 0-32-14.3-32-32s14.3-32 32-32H416c17.7 0 32 14.3 32 32z"/></svg>
        </div>
            <div className={navbar ? 'navbar-left' : 'navbar-left-closed'} id="navbar-left">
                <div className="navbar-profile-img">
                    <div className="profile-img"  onClick={toggleEditL}>
                        
                        {imageload && <>
                            <img src={url + slogo} style={{width: '80%'}}/>
                        </>}
                        <h1>edit</h1>
                    </div>
                </div>
                <div className="list-container">
                    <HashLink className="list-edit" smooth to={'/edit#profil'}>Profil</HashLink>
                    <HashLink className="list-edit" smooth to={'/edit#Kategori'}>Kategori Produk</HashLink>
                    <HashLink className="list-edit" smooth to={'/edit#office'}>Office</HashLink>
                    <HashLink className="list-edit" smooth to={'/edit#Mitra'}>Mitra</HashLink>
                    <HashLink className="list-edit" smooth to={'/edit#Partner'}>Partner</HashLink>
                    <HashLink className="list-edit" smooth to={'/edit#kontak'}>Kontak</HashLink>
                </div>
                <h1 className="logout" onClick={toggleLogout}>Logout</h1>
            </div>
            {editL && <>
                <div className="logo">
                    <div className="popup-box">
                        {load && <Loader/>}
                        <div className="box">
                        <span className="close-icon" onClick={toggleEditL}>x</span>
                            <div className="row">
                                {dataL.map((val) => {
                                    return(
                                        <div className="col">
                                        <div className="data-img-check">
                                            <img src={ischange ? logo64 : url + val.gambar_logo} style={{width: '80%', minWidth: '100px', minHeight: 'auto'}}/>
                                        </div>
                                        <label>{val.nama_logo}</label>
                                        <input required  type='file' accept="image/*" name="image" id="image" onChange={(e) => handleLogofChange(val.id_logo, e)}></input>
                                        </div>
                                    )
                                })}
                                
                            </div>
                        </div>
                    </div>
                </div>
                </>}
            {isLogoutOpen && <div className="popup-logout">
                <div className="popup-box">
                    <div className="box">
                        <h1 style={{color: 'black', fontSize: 'calc(10px + 1vw)'}}>Apakah Anda yakin untuk keluar?</h1>
                        <div className="row">
                        <h1 className="button logout" onClick={() => logout()}  id='profilbtn'>Logout</h1>
                        <h1 className="button" id='cancel-logout' onClick={() => toggleLogout()}>cancel</h1>
                        </div>
                </div>
            </div>
            </div>}
        </>
        
        : <Loader/>}
        
        </>
    )
}
