import ReactDOM, { render } from 'react-dom';
import React, { PureComponent, useRef, useState } from 'react';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import AvatarEditor from 'react-avatar-editor';
import Dropzone from 'react-dropzone';

const Crop = () => {
      const [image, setImage] = useState('');
      const cimage = useRef(null);
      const [scale, setScale] = useState(1);
    
      const handleGambardataChange = async (e) => {
        const fileK = e.target.files[0];
        const base64K = URL.createObjectURL(fileK);
        // setChgambardata(fileK);
        setImage(base64K);
        console.log(image);
    };

    const getImageUrl = async () => {
        const dataUrl = cimage.getImage().toDataURL()
        const result = await fetch(dataUrl)
        const blob = await result.blob()
      
        return window.URL.createObjectURL(blob)
      }
            
      const saveimage = async () => {
        const imageURL = await getImageUrl();
        console.log(imageURL);
      }
        return (
            <div>
                    
            <input required type='file' accept="image/*" name="gambardata" onChange={handleGambardataChange}></input>
            
            <AvatarEditor
                ref={cimage}
                image={image}
                width={250}
                height={250}
                border={50}
                color={[255, 255, 255, 0.6]} // RGBA
                scale={scale}
                rotate={0}
                style={{backgroundColor: 'black'}}
            />

            <input type='range' min={0.1} max={5} step={0.1} value={scale} onChange={(e) => setScale(e.target.value)}></input>
            
            <button onClick={saveimage}>Save</button>
            </div>
            
        )
}

export default Crop
