import React from 'react';
import 'react-image-crop/dist/ReactCrop.css';
import ReactCrop from 'react-image-crop';



export default class Crop extends React.Component {
    state = {
        src: null,
        crop: {
          unit: '%',
          width: 30,
          aspect: 16 / 9
        }
      };
    
      onImgChange = (e) => {
        if (e.target.files && e.target.files.length > 0) {
          const reader = new FileReader();
          reader.addEventListener('load', () =>
            this.setState({ src: reader.result })
          );
          reader.readAsDataURL(e.target.files[0]);
        }
      };
    
      onFileLoaded = (image) => {
        this.imageRef = image;
      };
    
      onImgCropComp = (crop) => {
        this.makeClientCrop(crop);
      };
    
      onImgCropChange = (crop, percentCrop) => {
        console.log("change")
        this.setState({ crop });
      };
    
      async makeClientCrop(crop) {
        if (this.imageRef && crop.width && crop.height) {
          const imgPath = await this.getResizeImage(
            this.imageRef,
            crop,
            'newFile.jpeg'
          );
          this.setState({ imgPath });
        }
      }
    
      getResizeImage(image, crop, fileName) {
        const canvas = document.createElement('canvas');
        const pixelRatio = window.devicePixelRatio;
        const scaleX = image.naturalWidth / image.width;
        const scaleY = image.naturalHeight / image.height;
        const ctx = canvas.getContext('2d');
    
        canvas.width = crop.width * pixelRatio * scaleX;
        canvas.height = crop.height * pixelRatio * scaleY;
    
        ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
        ctx.imageSmoothingQuality = 'high';
    
        ctx.drawImage(
          image,
          crop.x * scaleX,
          crop.y * scaleY,
          crop.width * scaleX,
          crop.height * scaleY,
          0,
          0,
          crop.width * scaleX,
          crop.height * scaleY
        );
    
        return new Promise((resolve, reject) => {
          canvas.toBlob(
            (blob) => {
              if (!blob) {
                //reject(new Error('Canvas is empty'));
                console.error('Canvas is empty');
                return;
              }
              blob.name = fileName;
              window.URL.revokeObjectURL(this.fileUrl);
              this.fileUrl = window.URL.createObjectURL(blob);
              resolve(this.fileUrl);
            },
            'image/jpeg',
            1
          );
        });
      }
    
      render() {
        const { crop, imgPath, src } = this.state;
    
        return (
          <div className="App">
            <div>
              <input type="file" accept="image/*" onChange={this.onImgChange} />
            </div>
            {src && (
              <ReactCrop
                ruleOfThirds
                crop={crop}
                onImageLoaded={this.onFileLoaded}
                onChange={this.onImgCropChange}
                onComplete={this.onImgCropComp}
              >
                <img src={src} style={{width: '20vw'}}/>
              </ReactCrop>
            )}
            {imgPath && (
              <img alt="Crop" style={{ maxWidth: '400px' }} src={imgPath} />
            )}
          </div>
        );
      }
}
