import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import "./edit.css";
import "./editProfil.css";
import "./editKategori.css";
import "./editOffice.css";
// import axios, { Axios } from "axios";
import Axios from "axios";
import Sectionimage from "./sectionimage";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { getMe, LogOut, reset } from "./auth";
import Navbar from "./navbar";
import Crop from "./imageresize";
import Loader from "./loader";
import SectionTable from "./sectiontable";
import { Editor } from "@tinymce/tinymce-react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import "./sectiontable.css";
import Office from "./office";
import Water from "./water";
import Profil from "./profil";

export default function Edit() {
  const { isError, isSuccess } = useSelector((state) => state.auth);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [profil, setProfil] = useState("");
  const [image, setImage] = useState(null);
  const [visi, setVisi] = useState("");
  const [misi, setMisi] = useState("");
  const [alamatfooter, setAlamatfooter] = useState("");
  const [emailfooter, setEmailfooter] = useState("");
  const [telpfooter, setTelpfooter] = useState("");
  const [whatsappfooter, setWhatsappfooter] = useState("");
  const [load, setLoad] = useState(true);
  const [toggleV, setToggleV] = useState(false);
  const [toggleM, setToggleM] = useState(false);
  const [ischange, setIschange] = useState(false);
  const [file, setFile] = useState(null);
  const [chgambardata64, setChgambardata64] = useState(null);

  const getProfil = async () => {
    const get = await Axios.put("http://192.168.6.235:3001/getprofil", {
      id: 1,
    }).then((response) => {
      const data = response.data;
      {
        data.map((val) => {
          setProfil(val.profildesc);
        });
      }
    });
    setLoad(false);
  };
  const getVisi = async () => {
    Axios.put("http://192.168.6.235:3001/getprofil", { id: 2 }).then(
      (response) => {
        const data = response.data;
        {
          data.map((val) => {
            setVisi(val.profildesc);
          });
        }
      }
    );
  };
  const getMisi = async () => {
    Axios.put("http://192.168.6.235:3001/getprofil", { id: 3 }).then(
      (response) => {
        const data = response.data;
        {
          data.map((val) => {
            setMisi(val.profildesc);
          });
        }
      }
    );
  };

  const getImage = async (id) => {
    // setLoad(true);
    console.log(id);
    let get;
    get = await Axios.put("http://192.168.6.235:3001/getprofil", {
      id: id,
    }).then((response) => {
      const data = response.data;
      {
        data.map((val) => {
          let url = "http://192.168.6.235:3001/" + val.gambar_profil;
          console.log(url);
          setImage(url);
        });
      }
    });
    // setLoad(false);
    if (id === 2) if (toggleV === false) getToggleV();
    if (id === 3) {
      if (toggleM === false) {
        console.log("jalan");
        getToggleM();
      }
    }
  };

  const getToggleV = () => {
    setToggleV(!toggleV);
    // if (toggle === false) setImage(null);
  };

  const getToggleM = () => {
    setToggleM(!toggleM);
    // if (toggle === false) setImage(null);
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);
      };
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

  const handleLogofChange = async (id, nama, e) => {
    e.preventDefault();
    setProgressBarVisibility(true);
    try {
      // const base64K = await convertToBase64(e.target.files[0]);
      // setLogof64(base64K);
      // console.log(base64K);
      // const set = await setLogof(e.target.files[0]);
      console.log(e.target.files[0]);
      const formData = new FormData();
      formData.append("id", id);
      formData.append("name", nama);
      formData.append("image", e.target.files[0]);
      const update = await updateData(formData);
      setProgressBarVisibility(false);
      if (showProgressBar === false) {
        getImage(id);
      }
    } catch {
      setProgressBarVisibility(false);
    }
  };

  const updateData = async (data) => {
    const updated = await Axios({
      method: "post",
      url: "http://192.168.6.235:3001/updateimageprofil",
      data: data,
      headers: { "Content-Type": "multipart/form-data" },
    });
  };

  const getFooter = async () => {
    Axios.get("http://192.168.6.235:3001/getfooter").then((response) => {
      const dataf = response.data;
      {
        dataf.map((val) => {
          setAlamatfooter(val.jalan_kantor);
          setEmailfooter(val.email);
          setTelpfooter(val.no_telp);
          setWhatsappfooter(val.whatsapp);
        });
      }
    });
  };

  const editorRef = useRef(null);
  const log = () => {
    if (editorRef.current) {
      console.log(editorRef.current.getContent());
    }
  };

  useEffect(() => {
    // console.log(isSuccess);
    dispatch(getMe());
  }, [dispatch]);

  useEffect(
    () => {
      // console.log(isError + " " + isSuccess);
      if (isError) {
        navigate("/");
      }
      // if(width < 930) setNavbar(false);
      getProfil("");
      // console.log(profil);
      getVisi("");
      // console.log(visi);
      getMisi("");
      // console.log(misi);
      // console.log(office);
      getFooter([]);
    },
    [isError],
    [],
    []
  );

  const handleProfilChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setProfil(html);
    const btn = document.getElementById("profilbtn");
    btn.style = "color: white; background-color: green; display: flex";
  };

  const handleVisiChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setVisi(html);
    const btn = document.getElementById("visibtn");
    btn.style = "color: white; background-color: green; display: flex";
    const btn2 = document.getElementById("editvisi");
    btn2.style = "display: none";
  };

  const handleMisiChange = (html) => {
    window.onbeforeunload = () => "unsaved";
    setMisi(html);
    const btn = document.getElementById("misibtn");
    btn.style = "color: white; background-color: green; display: flex";
    const btn2 = document.getElementById("editmisi");
    btn2.style = "display: none";
  };

  const updateProfil = async () => {
    setProgressBarVisibility(true);
    const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {
      profildesc: profil,
      id: 1,
    });
    setProfil(profil);
    const btn = document.getElementById("profilbtn");
    btn.style = "display: none";
    setProgressBarVisibility(false);
  };

  const updateVisi = async () => {
    setProgressBarVisibility(true);
    const update = await Axios.put("http://192.168.6.235:3001/updateprofil", {
      profildesc: visi,
      id: 2,
    });
