                        <input readOnly type="text" value={val.jalan}></input>
                        <div className="row">
                          <div className="col">
                            <label>Latitude</label>
                            <input
                              readOnly
                              type="text"
                              value={val.latitude}
                            ></input>
                          </div>
                          <div className="col">
                            <label>Longitude</label>
                            <input
                              readOnly
                              type="text"
