import Axios from "axios";
import { useEffect, useState } from "react";

const Sectionimage = props => {
    const [data, setdata] = useState([]);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [isAddOpen, setIsAddOpen] = useState(false);
    const [chnamadata, setChnamadata] = useState("");
    const [chgambardata, setChgambardata] = useState("");
    const [chiddata, setChiddata] = useState("");
    const [nfound, setNfound] = useState(false);
    const [search, setSearchK] = useState("");
    const gambar = "val." + props.gambar;


    const getdata = async (id) => {
        let tek2 = "%"+id+"%";
        console.log(tek2);
        Axios.put("http://localhost:3001/"+props.getdb, {nama: tek2})
        .then((response) => {
            if(response.data.length == 0) setNfound(true);
            else setNfound(false);
            setdata(response.data);
        })
    };

    function handleChangeSearchK (event) {
        event.preventDefault();
        console.log(search);
        getdata(search);
    }

    useEffect(() => {
        getdata([]);
    }, [])

    const toggleEditKPopup = () => {
        setIsEditOpen(!isEditOpen);
        if(isEditOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
        }
    }

    const toggleAddKPopup = () => {
        setIsAddOpen(!isAddOpen);
        if(isAddOpen === false){
            setChgambardata("");
            setChiddata("");
            setChnamadata("");
        }
    }

    const getdataId = (id) => {
        Axios.put("http://localhost:3001/"+ props.getdbId, {id: id})
            .then((response) => {
                const data = response.data;
                {data.map((val)=>{
                    setChiddata(val.id_data);
                    console.log(chiddata);
                    setChnamadata(val.nama_data);
                    console.log(chnamadata);
                    setChgambardata(val.gambar_data);
                    console.log("jalan")
                })}
            })
       
        toggleEditKPopup();
    }

    const handleNamadataChange = (event) => {
        setChnamadata(event.target.value);
    }

    const handleGambardataChange = async (e) => {
        const fileK = e.target.files[0];
        const base64K = await convertToBase64(fileK);
        setChgambardata(base64K);
        
        console.log(chgambardata);
    }

    const updatedata = (id, e) => {
        e.preventDefault();
        console.log("hai")
        Axios.put("http://localhost:3001/" + props.update, {namadata: chnamadata, gambardata: chgambardata, id : id})
        // getdata();
        window.location.reload();
        // document.getElementById('data').
        // .then((response) => {
        //     setdata(
        //         data.map((val) => {
        //             return val.id_data == id
        //             ? {
        //                 id: val.id_data,
        //                 nama_data: chnamadata,
        //                 gambar_data: chgambardata
        //             } : val;
        //         })
        //     )
        // })
        toggleEditKPopup();
};

const adddata = (e) => {
    e.preventDefault();
    console.log("hai");
    Axios.post("http://localhost:3001/"+ props.add, {namadata: chnamadata, gambardata: chgambardata})
        // getdata();
        window.location.reload();
}

const deletedata = (id) => {
    console.log("hai");
    Axios.put("http://localhost:3001/" + props.delete, {id: id});
    window.location.reload();
}

const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);
      fileReader.onload = () => {
        resolve(fileReader.result);
      };
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

    return(
        <div className="visimisi-container">
                        <div className="row">
                        <h1 className="profil">{props.title}</h1>
                        <form onSubmit={(event) => handleChangeSearchK(event)}>
                            <input type='text' placeholder={"search "+props.title} value={search} onChange={(event) => setSearchK(event.target.value)}></input>
                        </form>
                        <h1 className="button add" onClick={() => toggleAddKPopup()} id='misibtn'>tambah</h1>
                        </div>
                        {isAddOpen && <>
                            <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleAddKPopup}>x</span>
                                                <h1>Tambah {props.title}</h1>
                                                <form onSubmit={(event) => adddata(event)} encType="multipart/form-data">
                                                    <div className="data-img-check">
                                                         <img src={chgambardata} style={{maxWidth: "20vw"}}/>
                                                    </div>
                                                    <input required type='text' placeholder={"nama " + props.title} value={chnamadata} onChange={handleNamadataChange}></input>
                                                    <input required type='file' accept="image/*" name="gambardata" onChange={handleGambardataChange}></input>
                                                    {/* <textarea required rows={5} placeholder="link gambar..." value={chgambardata} onChange={handleGambardataChange}></textarea> */}
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                        </>}
                        <div className="container-row" id="data">
                            {nfound && <>
                                <h1 className="not-found">data not found</h1>
                            </>}
                            {data.map((val) => {
                                return(
                                <div className="edit-data-container">
                                    <div className="data-img-check section">
                                        <img src={val[props.gambar]} style={{maxWidth: "20vw"}}/>
                                    </div>
                                    <div className="col">
                                        <h1>{val[props.nama]}</h1>
                                        <h1 className="button edit" onClick={() => getdataId(val[props.id])}>edit</h1>
                                        <h1 className="button delete" onClick={() => deletedata(val[props.id])}>delete</h1>
                                    </div>
                                    {isEditOpen && <>
                                        <div className="popup-box">
                                            <div className="box">
                                                <span className="close-icon" onClick={toggleEditKPopup}>x</span>
                                                <h1>Edit data</h1>
                                                <form onSubmit={(event) => updatedata(chiddata, event)}>
                                                    <div className="data-img-check">
                                                        <img src={chgambardata} style={{maxWidth: "20vw"}}/>
                                                    </div>
                                                    <input required type='text' placeholder="nama data" value={chnamadata} onChange={handleNamadataChange}></input>
                                                    <input type='file' onChange={handleGambardataChange}></input>
                                                    <input type='submit' value='save'></input>
                                                </form>
                                            </div>
                                        </div>
                                    </>}
                                </div>
                                );
                            })}
                            
                        </div>
                    </div>
    )

}

export default Sectionimage;
